package user.select.carlosanguiano.selectuser;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.util.Log;import android.widget.Toast;import java.util.List;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;import user.select.carlosanguiano.selectuser.api.ApiClient;import user.select.carlosanguiano.selectuser.interfaces.ApiInterface;import user.select.carlosanguiano.selectuser.model.ResponseModel;public class MainActivity extends AppCompatActivity {    private static final String TAG = MainActivity.class.getSimpleName();    ApiInterface apiInterface;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        apiInterface = ApiClient.getClient().create(ApiInterface.class);        valuesResponse();    }    private void valuesResponse() {        Call<List<ResponseModel>> call = apiInterface.doGetListResources();        call.enqueue(new Callback<List<ResponseModel>>() {            @Override            public void onResponse(Call<List<ResponseModel>> call, Response<List<ResponseModel>> response) {                if (response.code() == 200) {                    if (response.body() != null & response.body().size() > 0) {                        Toast.makeText(MainActivity.this, "YES " + response.code() + " Size: " + response.body().size(), Toast.LENGTH_SHORT).show();                    }                }            }            @Override            public void onFailure(Call<List<ResponseModel>> call, Throwable t) {                Toast.makeText(MainActivity.this, "NOT" + t.getMessage(), Toast.LENGTH_SHORT).show();                Log.i(TAG, "onFailure: ");            }        });    }/*    private void valuesResponse() {        Call<ResponseModel> call = apiInterface.doGetListResources();        call.enqueue(new Callback<ResponseModel>() {            @Override            public void onResponse(Call<ResponseModel> call, Response<ResponseModel> response) {                Log.i(TAG, "onResponse: ");            }            @Override            public void onFailure(Call<ResponseModel> call, Throwable t) {                Log.i(TAG, "onFailure: ");            }        });    }*/}