package user.select.carlosanguiano.selectuser;import android.util.Log;import android.widget.TextView;import android.widget.Toast;import java.util.List;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;import user.select.carlosanguiano.selectuser.api.RetrofitHelper;import user.select.carlosanguiano.selectuser.interfaces.ApiInterface;import user.select.carlosanguiano.selectuser.model.ResponseModel;import user.select.carlosanguiano.selectuser.presentation.activity.BaseActivity;public class MainActivity extends BaseActivity {    private static final String TAG = MainActivity.class.getSimpleName();    ApiInterface apiInterface;    @Override    public int getContentLayout() {        return R.layout.activity_main;    }    @Override    public void initComponents() {        apiInterface = RetrofitHelper.getClient().create(ApiInterface.class);        valuesResponse();        TextView txtIO = findViewById(R.id.txtIO);    }    private void valuesResponse() {        Call<List<ResponseModel>> call = apiInterface.doGetListResources();        call.enqueue(new Callback<List<ResponseModel>>() {            @Override            public void onResponse(Call<List<ResponseModel>> call, Response<List<ResponseModel>> response) {                if (response.code() == 200) {                    if (response.body() != null & response.body().size() > 0) {                        Toast.makeText(MainActivity.this, "YES " + response.code() + " Size: " + response.body().size(), Toast.LENGTH_SHORT).show();                    }                }            }            @Override            public void onFailure(Call<List<ResponseModel>> call, Throwable t) {                Toast.makeText(MainActivity.this, "NOT" + t.getMessage(), Toast.LENGTH_SHORT).show();                Log.i(TAG, "onFailure: ");            }        });    }}