package user.select.carlosanguiano.selectuser;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.util.Log;import android.widget.Toast;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;import user.select.carlosanguiano.selectuser.api.ApiClient;import user.select.carlosanguiano.selectuser.interfaces.ApiInterface;import user.select.carlosanguiano.selectuser.model.ResponseModel;public class MainActivity extends AppCompatActivity {    private static final String TAG = MainActivity.class.getSimpleName();    ApiInterface apiInterface;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        apiInterface = ApiClient.getClient().create(ApiInterface.class);        valuesResponse();    }    private void valuesResponse() {        retrofit2.Call<ResponseModel> call = apiInterface.doGetListResources();        call.enqueue(new Callback<ResponseModel>() {            @Override            public void onResponse(retrofit2.Call<ResponseModel> call, Response<ResponseModel> response) {                Log.i(TAG, "onResponse: ");                String displayResponse = "";                ResponseModel responseModel = response.body();                String txtName = responseModel.getName();                String txtEmail = responseModel.getEmail();                Toast.makeText(MainActivity.this, txtName + txtEmail, Toast.LENGTH_SHORT).show();            }            @Override            public void onFailure(Call<ResponseModel> call, Throwable t) {                Log.i(TAG, "onFailure: ");            }        });    }}