package user.select.carlosanguiano.selectuser.mainactivity;import android.util.Log;import android.widget.TextView;import android.widget.Toast;import java.util.List;import retrofit2.Response;import user.select.carlosanguiano.selectuser.R;import user.select.carlosanguiano.selectuser.base.BaseActivity;import user.select.carlosanguiano.selectuser.model.ResponseModel;public class MainActivity extends BaseActivity implements MainMvpView {    private static final String TAG = MainActivity.class.getSimpleName();    private MainPresenter<MainActivity> mainPresenter;    @Override    public int getContentLayout() {        return R.layout.activity_main;    }    @Override    public void initComponents() {        TextView txtIO = findViewById(R.id.txtIO);        mainPresenter = new MainPresenter<>();        mainPresenter.onAttach(this);        mainPresenter.loadValueResponse();    }    @Override    public void openMainActivity() {        Log.i(TAG, "openMainActivity: ");    }    @Override    public void dataLoad(Response<List<ResponseModel>> response) {        Toast.makeText(MainActivity.this, "YES " + response.code() + " Size: " + response.body().size(), Toast.LENGTH_SHORT).show();    }    @Override    public void dataLoadFailed(Throwable t) {        Toast.makeText(MainActivity.this, "NOT " + t, Toast.LENGTH_SHORT).show();    }}