package user.select.carlosanguiano.selectuser.mainactivity;import android.util.Log;import java.util.List;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;import user.select.carlosanguiano.selectuser.api.RetrofitHelper;import user.select.carlosanguiano.selectuser.base.BasePresenter;import user.select.carlosanguiano.selectuser.interfaces.ApiInterface;import user.select.carlosanguiano.selectuser.model.ResponseModel;/** * Created by Carlos Anguiano on 4/4/18. * For more info contact: c.joseanguiano@gmail.com */class MainPresenter<V extends MainMvpView> extends BasePresenter<V> implements MainMvpPresenter<V> {    private ApiInterface apiInterface;    @Override    public void decideNextActivity() {        Log.i("TAG", "decideNextActivity: ");        getMVPView().openMainActivity();    }    void loadValueResponse() {        apiInterface = RetrofitHelper.getClient().create(ApiInterface.class);        Call<List<ResponseModel>> call = apiInterface.doGetListResources();        call.enqueue(new Callback<List<ResponseModel>>() {            @Override            public void onResponse(Call<List<ResponseModel>> call, Response<List<ResponseModel>> response) {                if (response.code() == 200) {                    if (response.body() != null & response.body().size() > 0) {                        getMVPView().dataLoad(response);                    }                }            }            @Override            public void onFailure(Call<List<ResponseModel>> call, Throwable t) {                getMVPView().dataLoadFailed(t);            }        });    }}